import dispatcher from './phone-dispatcher';
import {EventEmitter} from 'events';

const chatStoreChangeEvent = 'phone-store-change-event';

class PhoneStore extends EventEmitter {

 	constructor(){
 		super();

 		dispatcher.register(this.handleAction.bind(this));

 		this._hotelList = [{hotelName: 'Hilton Chicago', hotelAddress: '720 South Michigan Avenue', hotelCity: 'Chicago', hotelState: 'Illinois', hotelZip: '60605'}];
 	}

 	get hotelList(){
 		return this._hotelList;
 	}

 	handleAction(action){

 	}

 	_checkSubmitNewMessage(){
 		if (this._newMessage.trim().length > 0) {
			let message = {
				id: Date.now(),
				content: this._newMessage
			};

			this._hotelList.push(message);
			this._newMessage = '';

			// this.setState({
			// 	messageList: this.props.messageList,
			// 	newMessage: ''
			// });
		}
 	}

 	emitChange(){
 		this.emit(chatStoreChangeEvent);
 	}

 	addEventListener(callback){
 		this.on('chat-store-change-event', callback);
 	}

 	removeEventListner(callback){
 		this.removeListener(chatStoreChangeEvent, callback);
 	}

}

let phoneStore = new PhoneStore();

export default phoneStore;
