import React from 'react';
import chatStore from './phone-store.js';
import ChatContainer from './phone-container.react'; 
import ChatMetrics from './phone-metrics.react';

export default React.createClass({

	getInitialState(){
		return this.getState();
	},

	getState(){
		return {
			newMessage: chatStore.newMessage,
			messageList: chatStore.messageList
		}
	},

	render(){
		// return <ChatContainer newMessage={this.state.newMessage} messageList={this.state.messageList />;
		return (
			<div className='chat-controller'>
			<ChatContainer {...this.state} />
			<ChatMetrics {...this.state} />
			</div>
		);
	},

	componentDidMount(){
		chatStore.addEventListener(this.handleChange);
	},

	handleChange(){
		this.setState(this.getState());
	},

	componentWillUnmount(){
		chatStore.removeEventListner(this.handleChange);
	}

});
